import React, { useState } from 'react';
import '../style/Bieudo.css';
import bieudo from '../assets/bieudo.png';

const tabs = ['Tài sản', '10 Giá', 'Lịch sử giao dịch', 'Mã phái sinh'];

function Bieudo() {
  const [activeTab, setActiveTab] = useState('Tài sản');

  return (
    <div className="bieudo">
      <div className="bieudo-container">
        {/* Left: image */}
        <div className="bieudo-image">
          <img src={bieudo} alt="Biểu đồ" />
        </div>

        {/* Right: Tabs + Content */}
        <div className="bieudo-info-panel">
          <div className="bieudo-tabs">
            {tabs.map((tab) => (
              <div
                key={tab}
                className={`bieudo-tab ${activeTab === tab ? 'active' : ''}`}
                onClick={() => setActiveTab(tab)}
              >
                {tab}
              </div>
            ))}
          </div>

          <div className="bieudo-content">
            {activeTab === 'Tài sản' && (
              <div className="bieudo-asset-info">
                <div className="info-row">
                  <span>Sức mua (357007)</span>
                  <span>--</span>
                </div>
                <div className="info-row">
                  <span>Lãi/Lỗ chưa đóng</span>
                  <span>--</span>
                </div>
                <div className="info-row">
                  <span>Tổng tài sản</span>
                  <span>120,000,0000</span>
                </div>
                <div className="info-row">
                  <span>Tiền mặt</span>
                  <span>--</span>
                </div>
                <div className="info-row">
                  <span>Tiền ký quỹ tại VSD</span>
                  <span>--</span>
                </div>
              </div>
            )}

            {activeTab !== 'Tài sản' && (
              <div className="bieudo-placeholder">
                Nội dung tab "{activeTab}" đang được phát triển...
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Bieudo;
