import React, { useState } from 'react';
import '../style/OrderForm.css';
import Option from '../assets/Option.png';

function OrderForm() {
  const [stockCode, setStockCode] = useState('VN30F2501');
  const [orderType, setOrderType] = useState('Lệnh thường');
  const [orderMode, setOrderMode] = useState('ATO');
  const [price, setPrice] = useState(1220.3);
  const [quantity, setQuantity] = useState(1000);
  const [position, setPosition] = useState('Long');

  return (
    <div className="order-form">
      <div className="setup">
        <h3 className="gradient" style={{color: '#6142FE'}}>Giao dịch</h3>
        <h3 className="padding">Cài đặt</h3>
      </div>
      <label className="title">Mã chứng khoán</label>
      <select className="option" value={stockCode} onChange={(e) => setStockCode(e.target.value)}>
        <option>VN30F2501</option>
        <option>option2</option>
      </select>

      <div className="order-type">
        <button className="Lenh"> Lệnh thường </button>
        <button className="lenh">
          Lệnh điều kiện
          <img src={Option} alt="option" className="ok"></img>
        </button>
      </div>

      <label>Loại lệnh</label>
      <div className="order-mode">
        {['ATO', 'ATC', 'MOK', 'MTL', 'MAK'].map((mode) => (
          <button
            key={mode}
            className={orderMode === mode ? 'active' : ''}
            onClick={() => setOrderMode(mode)}
          >
            {mode}
          </button>
        ))}
      </div>

      <label>Giá đặt</label>
      <div className="price-input">
        <button onClick={() => setPrice((p) => Math.max(0, p - 1))}>-</button>
        <input
          type="number"
          value={price}
          onChange={(e) => setPrice(Number(e.target.value))}
          step="0.1"
        />
        <button onClick={() => setPrice((p) => p + 1)}>+</button>
      </div>

      <label>Khối lượng</label>
      <div className="quantity-input">
        <button onClick={() => setQuantity((q) => Math.max(0, q - 100))}>-</button>
        <input
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(Number(e.target.value))}
          step="100"
        />
        <button onClick={() => setQuantity((q) => q + 100)}>+</button>
      </div>

      <div className="position-buttons">
        <button
          className={position === 'Long' ? 'long' : ''}
          onClick={() => setPosition('Long')}
        >
          Long
        </button>
        <button
          className={position === 'Short' ? 'short' : ''}
          onClick={() => setPosition('Short')}
        >
          Short
        </button>
      </div>
    </div>
  );
}

export default OrderForm;
