import React from 'react';

const marketData = [
  { name: 'VN-INDEX', value: '1,283.26', change: '(15.96 1.26%)', color: 'green', shares: '895,786,314', status: 'Chờ nhận lệnh' },
  { name: 'VN30', value: '1,372.04', change: '(19.79 1.46%)', color: 'green', shares: '482,264,152', status: 'Chờ nhận lệnh' },
  { name: 'VNXALL', value: '905,524,492', change: '(0/0%)', color: 'red', shares: '21,524,209 Tỷ', status: 'Chờ nhận lệnh' },
  { name: 'HNX-INDEX', value: '216.04', change: '+1.91 (0.89%)', color: 'green', shares: '56,598,636', status: 'Chờ nhận lệnh' },
  { name: 'HNX-UPCOMINDEX', value: '93.59', change: '+0.19 (0.20%)', color: 'green', shares: '46,870,777', status: 'Chờ nhận lệnh' },
];

function MarketSummary() {
  return (
    <div className="market-summary">
      {marketData.map((item) => (
        <div key={item.name} className="market-item">
          <div className="name">{item.name}</div>
          <div className={`value ${item.color === 'green' ? 'green' : 'red'}`}>{item.value} {item.change}</div>
          <div className="shares">{item.shares} cổ phần</div>
          <div className="status">{item.status}</div>
        </div>
      ))}
    </div>
  );
}

export default MarketSummary;
